void draw_char(SDL_Surface* surface, unsigned char symbol, int x, int y, unsigned short color) {
	x += (8 - 1) * 1;
	int flip = 0;
	if(symbol > 127) { // flip symbols over 128
		flip = 1;
		symbol -= 128;
	}
	const unsigned char* ptr = embedded_font + symbol * 8;

	for(int i = 0, ys = 0; i < 6; i++, ptr++, ys += 1)
		for(int col = 8 - 6, xs = x - col; col < 8; col++, xs -= 1)
			if((*ptr & 1 << col) && y + ys < surface->h && xs < surface->w )
				((unsigned short*)surface->pixels)[(y + flip * 4 + (1 - 2 * flip) * ys) * (surface->pitch >> 1) + xs] = color;

}

void draw_string(SDL_Surface* surface, const char* text, int orig_x, int orig_y, unsigned short color) {
	int x = orig_x, y = orig_y;
	while(*text) {
		if(*text == '\n') {
			x = orig_x;
			y += 8;
		} else {
			draw_char(surface, *text, x, y, color);
			x += 6;
		}
		text++;
	}
}

int main(int argc, char* argv[]) {

	if (SDL_Init(SDL_INIT_VIDEO) < 0) {
		// Initialization failed
		printf("SDL initialisation failed: %s\n", SDL_GetError());
		return 1;
	}

	// Create a window
#ifdef SDL_2
	SDL_DisplayMode current;
	SDL_GetCurrentDisplayMode(0, &current);
	SDL_Window *Screen = SDL_CreateWindow("Test Joystick", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, current.w, current.h, SDL_WINDOW_FULLSCREEN);
    if (!Screen) {
		fprintf(stderr, "SDL_CreateWindow failed: %s\n", SDL_GetError());
		return 1;
	}
	//Get window surface
    SDL_Surface* screen = SDL_GetWindowSurface(Screen);
#else
	SDL_Surface* screen = SDL_SetVideoMode(640, 480, 16, SDL_FULLSCREEN|SDL_HWSURFACE);
#endif
	if (!screen) {
		fprintf(stderr, "SDL video mode set failed: %s\n", SDL_GetError());
		return 1;
	}

	// Create a back buffer 300 x 80
	SDL_Surface* backbuffer = SDL_CreateRGBSurface(SDL_SWSURFACE, 300, 80, 16, 0, 0, 0, 0);
	if (!backbuffer) {
		fprintf(stderr, "SDL_CreateRGBSurface failed: %s\n", SDL_GetError());
		return 1;
	}

	SDL_FillRect(backbuffer, NULL, SDL_MapRGB(backbuffer->format, 0, 0, 0)); // Clear the backbuffer with black.
	draw_string(backbuffer, "Hello world SDL in full screen 640x480", 10, 10, SDL_MapRGB(backbuffer->format, 255, 255, 255));
	draw_string(backbuffer, "This is embedded font in GREEN", 10, 20, SDL_MapRGB(backbuffer->format, 0, 255, 0));
	draw_string(backbuffer, "This is embedded font in BLUE", 10, 30, SDL_MapRGB(backbuffer->format, 0, 0, 255));
	draw_string(backbuffer, "Press ESC or ANY button to quit", 10, 40, SDL_MapRGB(backbuffer->format, 255, 255, 0));

	// Main loop
	int quit = 0;
	while (!quit) {
		SDL_Event ev;

		while (SDL_PollEvent(&ev)) {
			switch (ev.type) {
				case SDL_QUIT:
					quit = 1;
					break;
				case SDL_KEYDOWN:
					if (ev.key.keysym.sym == SDLK_ESCAPE)
						quit = 1;
					break;
				default:
					break;
			}
		}
		SDL_BlitSurface(backbuffer, NULL, screen, NULL);
#ifdef SDL_2
		SDL_UpdateWindowSurface(Screen); //Update the surface
#else
		SDL_Flip(screen);
#endif
	}

	// Clean up
#ifdef SDL_2
    SDL_DestroyWindow(Screen);
#endif	
	SDL_FreeSurface(backbuffer);
	SDL_Quit();

	return 0;
}